<?phpclass Pagination{	public $html;    public function __construct($page,$count,$limit,$url){		$row_remain = ($count%$limit);		$page_num = ceil($count/$limit);		// $page_num = 10;		if($row_remain>0){			$page_num++;		}		$query_str = $_SERVER["QUERY_STRING"];		$lang_page = array(			"first"=>"First",			"previous"=>"Prev",			"next"=>"Next",			"last"=>"Last",		);		$chr = "&";				if(isset($_GET["page"]))			$query_str = str_replace("page=".$_GET["page"],"",$query_str);		if(empty($query_str))			$chr = "?";            		$page = intval(isset($_GET["page"])?$_GET["page"]:0);		$url = $url.$query_str.$chr."page=";		$this->html = '<nav class=""><ul class="pagination justify-content-end">';		if($page_num>0){			// First			$disabled_first = ($page>1) ? '' : 'disabled';			$this->html.= '<li class="page-item '.$disabled_first.'"><a  class="page-link"  href="'.$url.'1"> '.$lang_page['first'].' </a></li>';			// Fre			$disabled_previous = ($page>1&&(($page-2)<=$page_num))  ? '' : 'disabled';			$this->html.= '<li class="page-item '.$disabled_previous.'"><a  class="page-link"  href="'.$url.($page-2).'"> '.$lang_page['previous'].' </a></li>';			if($page_num<=5)			{								for($i=1;$i<=$page_num;$i++){					$txt = $i;					if($i==$page){						$this->html.= '<li class="page-item active "><a  class="page-link" >'.$txt.'</a></li>';					}else{						$this->html.= '<li class="page-item"><a  class="page-link" href="'.$url.$i.'">'.$txt.'</a></li>';					}				}			}else{				if(($page-1)>=1)				{					if(($page-2)>=1)					{						$this->html.='<li class="page-item"><a  class="page-link" >...</a></li>';					}					$this->html.= '<li class="page-item "><a  class="page-link" href="'.$url.($page-1).'">'.($page-1).'</a></li>';				}				$this->html.= '<li class="page-item active "><a  class="page-link" >'.$page.'</a></li>';				if(($page+1)<=$page_num)				{					$this->html.= '<li class="page-item "><a  class="page-link" href="'.$url.($page+1).'">'.($page+1).'</a></li>';					if(($page+2)<=$page_num)					{						$this->html.='<li class="page-item"><a  class="page-link" >...</a></li>';					}				}			}			$disabled_next = ($page<$page_num&&(($page+2)<=$page_num)) ? '' : 'disabled';			$this->html.= '<li class="page-item '.$disabled_next.'"><a  class="page-link"  href="'.$url.($page+2).'"> '.$lang_page['next'].' </a></li>';						// Last			$disabled_last = ($page<$page_num) ? '' : 'disabled';			$this->html.= '<li class="page-item '.$disabled_last.'"><a  class="page-link"  href="'.$url.$page_num.'"> '.$lang_page['last'].' </a></li>';		}		$this->html .= '</ul></nav>';    }}?>